<script setup>
import { ref, onMounted } from 'vue'

const typewriterElement = ref(null)
const text = 'Unleash your creativity,\nsoftware shapes the future.'

const typeWriter = (text, i) => {
  if (i < text.length) {
    if (text.charAt(i) === '\n') {
      typewriterElement.value.innerHTML += '<br>'
    } else {
      typewriterElement.value.innerHTML += text.charAt(i)
    }
    setTimeout(() => typeWriter(text, i + 1), 100) // 调整速度
  } else {
    console.log('[Successful] Finished printing.')
  }
}

onMounted(() => {
  typeWriter(text, 0)
})
</script>

